import React, {FC, useState} from "react";
import {Modal, message, Table, Image, Tag, TableColumnsType} from "antd";
import {Order, OrderGoods, OrderItem} from "@/types";
import useOrderGoodsItems from "@/hooks/useOrderGoodsItems";
import {ColumnsType} from "antd/es/table";
import useParameters from "@/hooks/useParameters";
import {
    formatDateTime,
    getColorWithStepAndIndex,
    getDepartmentAndNotesWithStepAndIndex,
    getNotesForOneProgress
} from "@/utils/utils";
import useRouterUtils from "@/hooks/useRouterUtils";
import {MarkProgressParam} from "@/requests/order";
import {record} from "zod";
import MarkProgressModal, {MarkProgressProps} from "@/components/order/MarkProgressModal";

interface Props {
    open: boolean,
    closeFn: (success: boolean) => void,
    order: Order | undefined,
    orderNo: string,
}


const OrderGoodsDetailModal: FC<Props> = (
    {
        open,
        closeFn,
        order,
        orderNo
    }
) => {
    const columns: ColumnsType<OrderGoods> = [
        {
            title: "商品编号",
            dataIndex: "goods_no",
        },
        {
            title: "名称",
            dataIndex: "name",
        },
        {
            title: "商品",
            dataIndex: "image",
            width: "80px",
            render: (_, record) => (
                <Image
                    width={60}
                    height={60}
                    src={record.image}
                    fallback="data:image/png;base64,"/>
            )
        },
        {
            title: "包装卡片",
            dataIndex: "package_card",
            width: "80px",
            render: (_, record) => (
                <Image
                    width={60}
                    height={60}
                    fallback="data:image/png;base64,"
                    src={record.package_card}/>
            )
        },
        {
            title: "包装卡片说明",
            dataIndex: "package_card_des",
        },
        {
            title: "流程进度",
            dataIndex: "step_count",
            render: (_, record) => {
                return (
                    <>
                        {record.steps.map(step => (
                            <Tag
                                key={`${record.id}-${step.step}-${step.index}`}
                                color={getColorWithStepAndIndex(step.step, step.index)}
                            >
                                <div className='text-black'>
                                    {getDepartmentAndNotesWithStepAndIndex(step.step, step.index)}: {step.count}
                                </div>
                            </Tag>
                        ))}
                    </>
                )
            }
        },
        {
            title: "操作",
            dataIndex: "action",
            render: (_, record) => {
                return (
                    <>
                        {record.is_next_action ? (
                            <a href='#' onClick={() => {
                                openMarkProgressModal({
                                    order_goods_id: record.id,
                                    order_item_id: 0,
                                    currentStep: record.current_step
                                })
                            }}>
                                标记流程
                            </a>
                        ) : null}
                    </>
                )
            }
        },
    ]
    const expandableColumns: TableColumnsType<OrderItem> = [
        {
            title: "sku_no",
            dataIndex: "sku_no",
        },
        {
            title: "颜色",
            dataIndex: "color",
            width: "120px",
        },
        {
            title: "数量",
            dataIndex: "count",
            width: "120px",
        },
        {
            title: "单位",
            dataIndex: "unit",
        },
        {
            title: "单价",
            dataIndex: "unit_price",
        },
        {
            title: "总价",
            dataIndex: "total_price",
        },
        {
            title: "流程进度",
            dataIndex: "steps",
            render: (_, record) => {
                return (
                    <>
                        {record.steps.map(step => (
                            <div
                                key={`${step.id}`}
                                className=''
                            >
                                {formatDateTime(new Date(step.dt))}: {step.account_name}({step.department}): {getNotesForOneProgress(step)}
                            </div>
                        ))}
                    </>
                )
            }
        },
        {
            title: "操作",
            dataIndex: "action",
            render: (_, record) => {
                return (
                    <>
                        {record.is_next_action ? (
                            <a href='#'>
                                标记流程
                            </a>
                        ) : null}
                    </>
                )
            }
        },
    ]

    const {page, pageSize} = useParameters();
    const {orderGoods, isError, isLoading, total} = useOrderGoodsItems(0, orderNo)
    const {reloadPage} = useRouterUtils();

    const [orderGoodsId, setOrderGoodsId] = useState<number>(0)
    const [orderItemId, setOrderItemId] = useState<number>(0)
    const [currentStep, setCurrentStep] = useState<number>(0)

    if (isError) {
        message.error(isError)
    }

    const openMarkProgressModal = (params: {
        order_goods_id: number,
        order_item_id: number,
        currentStep: number,
    }) => {
        setIsOpenMarkProgressModal(true)
        setOrderGoodsId(params.order_goods_id)
        setOrderItemId(params.order_item_id)
        setCurrentStep(params.currentStep)
    }
    const [isOpenMarkProgressModal, setIsOpenMarkProgressModal] = useState<boolean>(false);

    return (
        <Modal
            className='rounded-xl'
            width={'1500px'}
            open={open}
            title={'订单商品'}
            onCancel={(e) => {
                closeFn(false)
            }}
            closable={true}
            footer={null}
        >
            <MarkProgressModal
                open={isOpenMarkProgressModal}
                closeFn={() => setIsOpenMarkProgressModal(false)}
                order_goods_id={orderGoodsId}
                order_item_id={orderItemId}
                currentStep={currentStep}
            />

            <Table
                rowKey={'id'}
                className='mx-1 overflow-auto'
                size={"middle"}
                loading={isLoading}
                bordered={true}
                // loading={isLoading || (refresh && isValidating)}
                columns={columns}
                pagination={{total: total, current: page, pageSize: pageSize}}
                dataSource={orderGoods}
                expandable={{
                    // expandRowByClick: true,
                    columnTitle: "查看SKU",
                    expandedRowRender: ((record, index, indent, expanded) => (
                        <div className='p-2'>
                            <Table
                                rowKey={`${record.id}`}
                                bordered={true}
                                pagination={false}
                                dataSource={record.items}
                                columns={expandableColumns}
                            />
                        </div>
                    ))
                }}
                onChange={(pagination, filters, sorter) => {
                    reloadPage({
                        page: pagination.current,
                        pageSize: pagination.pageSize,
                    })
                }}
            />
        </Modal>
    )
}


export default OrderGoodsDetailModal
